css:
.product__short-stock {
display: none !important;
}
.__pages_site-fechado .header,
.__pages_site-fechado .footer {
    display: none !important;
}

.product-card__rating + .badge-dark, .product__rating + .product__out-of-stock  {
  font-size: 0px;
}
.product-card__rating + .badge-dark:before {
  color: #fff;
  background-color: #343a40;
  content: 'Esgotado';
  font-size: 1rem !important;
  display: inline-block;
  padding: .25em .4em;
  font-weight: 700;
  line-height: 1;
  text-align: center;
  white-space: nowrap;
  vertical-align: baseline;
  border-radius: .25rem;
}
.product__rating + .product__out-of-stock:before {
  content: 'Esgotado';
  font-weight: var(--font-bold);
  font-size: var(--font-size-lg);
  color: var(--warning);
}
